import Algorithms

struct Day6: Day {
  let dayOfMonth: Int = 6

  private func process(windowSize: Int) throws -> Int {
    let windows = input.windows(ofCount: windowSize)
    let offset = try windows
      .enumerated()
      .first(where: { _, window in Set(window).count == windowSize })
      .orThrow()
      .offset
    return offset + windowSize
  }

  func solution1() async throws -> Any {
    try process(windowSize: 4)
  }

  func solution2() async throws -> Any {
    try process(windowSize: 14)
  }

  let input: String = """
  
  """
}
